import "./StartProjectPage.css";
import vec from "./../../logos/Vector233.png";
import { useState } from "react";

export const StartProjectPage = () => {
  const [isActive, setIsActive] = useState(false);
  return (
    <div className='wrapper'>
      <div className='section'>
        <h2 className='section_title'>Start Project</h2>
        <p className='info_text'>What do you want?</p>
        <form action=''>
          <div className='proj_form'>
            <div className='question'>
              <label htmlFor=''>Price Offer</label>
              <input
                type='radio'
                default={true}
                name='type_of_project'
                onClick={() => {
                  setIsActive(false);
                  console.log(isActive);
                }}
              />
            </div>
            <div className='question'>
              <label htmlFor=''>Schedule a call</label>
              <input
                type='radio'
                name='type_of_project'
                onClick={() => {
                  setIsActive(true);
                  console.log(isActive);
                }}
              />
            </div>
          </div>
          <div className='second_form'>
            <div className={isActive ? "huge_div" : "second_question"}>
              <label htmlFor=''>
                {isActive ? "Preferred means of call" : "Project Type"}
              </label>
              <input type='select' placeholder='Select Project types' />
            </div>
            {isActive ? (
              ""
            ) : (
              <>
                <p className='form_sub_title'>Company Details</p>
                <input type='text' placeholder='*Company Name' />
                <textarea
                  name=''
                  id=''
                  rows='10'
                  placeholder='Tell us about your project'
                ></textarea>
                <div className='file_upload'>
                  <img src={vec} alt='' width={23} />
                  <label for='file-upload' className='custom-file-upload'>
                    Attach file
                  </label>
                  <input id='file-upload' type='file' />
                </div>
              </>
            )}

            <p className='form_sub_title'>Personal Details</p>
            <div className='there_form'>
              <div className='there_sub_form'>
                <input type='text' placeholder='*First Name' />
                <input type='text' placeholder='*Surname' />
              </div>
              <div className='there_sub_form'>
                <input type='text' placeholder='*Email' />
                <input type='text' placeholder='*Phone Number' />
              </div>
            </div>
            {isActive ? (
              <div className='data_time_sec'>
                <p className='form_sub_title'>Date & Time</p>
                <div className='there_form_sec'>
                  <input type='text' placeholder='Date' />
                  <input type='text' placeholder='Time' />
                </div>
              </div>
            ) : (
              ""
            )}
            <button className='form_button'>send</button>
          </div>
        </form>
      </div>
    </div>
  );
};
